@-webkit-keyframes bounce /* Safari 和 Chrome */
{
    0%{
        -webkit-transform: translate(-50%,-50%) scale(0.8,0.8);
    }
    50%{
        -webkit-transform: translate(-50%,-50%) scale(1.1,1.1);
    }
    75%{
        -webkit-transform: translate(-50%,-50%) scale(0.8,0.8);
    }
    100%{
        -webkit-transform: translate(-50%,-50%) scale(1,1);
    }
}
@-webkit-keyframes narrow /* Safari 和 Chrome */
{
    0%{
        -webkit-transform: translate(-50%,-50%) scale(1,1);
    }
    100%{
        -webkit-transform: translate(-50%,-50%) scale(0,0);
    }
}
#shadow-box{
    width: 100%;
    height: 100%;
    position: fixed;
    top: 0;
    z-index: 1000;
    left: 0;
}
#shadow{
    color:white;
    width: 6em;
    height: 5em;
    top: 50%;
    left: 50%;
    -webkit-transform: translate(-50%,-50%);
    /*margin-top: -2.5em;*/
    /*margin-left: -3em;*/
    position: absolute;
    line-height:5em;
    background-color: rgba(0,0,0,0.9);
    border-radius: 1em;
    text-align: center;
    opacity: 1;/*webkit开启硬件加速，优化动画卡顿*/
    -webkit-backface-visibility: hidden;/*webkit开启硬件加速，优化动画卡顿*/
    -webkit-box-shadow: 0 0 1.5em 0.1em rgba(0, 0, 0, 1);
    -webkit-animation: bounce 350ms ease;
}
#shadow img{
    width: 2.5em;
    height: 2.5em;
    margin-top: 1.25em;
}
